function urlFromBackgroundImage(el) {
    let bg = el.style.backgroundImage;

    let url = '';
    let start_adding = false;

    for(let i in bg) {
        let s = bg[i];

        if(s === '(') {
            start_adding = true;
        }

        if(s === ')') {
            start_adding = false;
            break;
        }

        if(start_adding) {
            url += s;
        }
    }

    url = url.replace(/[\(\'\"\)]/g, '');

    return url;
}

