function getWindowsPath (i, is_url, is_fs) {
    if(!i) {
        return null;
    }

    //only change url on windows
    if(!is_windows || i.startsWith('./')) {
        return i;
    }

    if(is_url) {
        i = i.replace(/\\/g, '/');

        let str = 'file://';

        if(!i.startsWith(str) && !i.startsWith('http')) {
            if(i.startsWith('/')) {
                i = str + i;
            } else {
                i = str + '/' + i;
            }
        }
    } else if(is_fs) {
        i = i.replace(/\//g, '\\');
    }

    return i;
}

